import { beforeEach, afterEach, describe, it, expect } from 'vitest';
import { SKIP_REAL_AGENT_TESTS } from './e2e-utils.js';
import {
  mkdtempSync,
  rmSync,
  writeFileSync,
  readFileSync,
  existsSync,
  mkdirSync,
  chmodSync,
} from 'node:fs';
import { tmpdir } from 'node:os';
import { join } from 'node:path';
import { execa } from 'execa';

/**
 * E2E tests for `rover list` (alias `rover ls`) command
 *
 * These tests run the actual rover CLI binary and test the full task listing workflow.
 * They mock system tool availability by creating wrapper scripts in a temporary bin directory.
 */

describe('rover list (e2e)', () => {
  let testDir: string;
  let originalCwd: string;
  let mockBinDir: string;
  let mockHomeDir: string;
  let originalPath: string;

  const createMockTool = (
    toolName: string,
    exitCode: number = 0,
    output: string = 'mock version 1.0.0'
  ) => {
    const scriptPath = join(mockBinDir, toolName);
    const scriptContent = `#!/usr/bin/env bash\necho "${output}"\nexit ${exitCode}`;
    writeFileSync(scriptPath, scriptContent);
    chmodSync(scriptPath, 0o755);
  };

  const createMockScript = (toolName: string, scriptContent: string) => {
    const scriptPath = join(mockBinDir, toolName);
    writeFileSync(scriptPath, scriptContent);
    chmodSync(scriptPath, 0o755);
  };

  const createMockClaude = () => {
    createMockScript(
      'claude',
      `#!/usr/bin/env bash
if [[ "$1" == "--version" ]]; then
  echo "Claude CLI v1.0.0"
  exit 0
fi

if [[ "$1" == "-p" ]]; then
  PROMPT=$(cat)
  if [[ "$2" == "--output-format" && "$3" == "json" ]]; then
    echo '{"result":"{\\"title\\":\\"Test task\\",\\"description\\":\\"A test task description.\\"}"}'
  else
    echo '{"title":"Test task","description":"A test task description."}'
  fi
  exit 0
fi

echo "Claude CLI v1.0.0"
exit 0
`
    );
  };

  const roverBin = join(__dirname, '../../../dist/index.mjs');

  const runRover = async (args: string[], options: { cwd?: string } = {}) => {
    const testPath = `${mockBinDir}:${originalPath}`;
    return execa('node', [roverBin, ...args], {
      cwd: options.cwd || testDir,
      env: {
        PATH: testPath,
        HOME: mockHomeDir,
        USER: process.env.USER,
        TMPDIR: process.env.TMPDIR,
        ROVER_NO_TELEMETRY: '1',
      },
      reject: false,
    });
  };

  beforeEach(async () => {
    originalCwd = process.cwd();
    originalPath = process.env.PATH || '';

    testDir = mkdtempSync(join(tmpdir(), 'rover-list-e2e-'));
    process.chdir(testDir);

    mockBinDir = join(testDir, '.mock-bin');
    mkdirSync(mockBinDir, { recursive: true });

    // Create mock HOME directory with agent configuration files
    mockHomeDir = join(testDir, '.mock-home');
    mkdirSync(mockHomeDir, { recursive: true });

    // Create mock Claude config file (required by task command validation)
    writeFileSync(
      join(mockHomeDir, '.claude.json'),
      JSON.stringify({ version: 1 })
    );

    process.env.PATH = `${mockBinDir}:${originalPath}`;

    createMockTool('docker', 127, 'command not found: docker');
    createMockTool('claude', 127, 'command not found: claude');
    createMockTool('codex', 127, 'command not found: codex');
    createMockTool('cursor', 127, 'command not found: cursor');
    createMockTool('cursor-agent', 127, 'command not found: cursor-agent');
    createMockTool('gemini', 127, 'command not found: gemini');
    createMockTool('qwen', 127, 'command not found: qwen');
    createMockTool('opencode', 127, 'command not found: opencode');

    createMockTool('docker', 0, 'Docker version 24.0.0');
    createMockClaude();

    // Initialize a real git repository
    await execa('git', ['init']);
    await execa('git', ['config', 'user.email', 'test@test.com']);
    await execa('git', ['config', 'user.name', 'Test User']);
    await execa('git', ['config', 'commit.gpgsign', 'false']);

    writeFileSync(
      'package.json',
      JSON.stringify(
        { name: 'test-project', version: '1.0.0', type: 'module' },
        null,
        2
      )
    );
    writeFileSync('README.md', '# Test Project\n');

    await execa('git', ['add', '.']);
    await execa('git', ['commit', '-m', 'Initial commit']);

    // Initialize rover
    await runRover(['init', '--yes']);
  });

  afterEach(() => {
    process.chdir(originalCwd);
    process.env.PATH = originalPath;
    rmSync(testDir, { recursive: true, force: true });
  });

  describe('project-scoped task listing', () => {
    it('should display tasks belonging to the current project', async () => {
      // Create a task first
      const taskResult = await runRover([
        'task',
        '-y',
        'Create a hello world script',
        '--json',
      ]);
      expect(taskResult.exitCode).toBe(0);

      // List tasks
      const listResult = await runRover(['list', '--json']);

      expect(listResult.exitCode).toBe(0);

      const output = JSON.parse(listResult.stdout);
      expect(Array.isArray(output)).toBe(true);
      expect(output.length).toBeGreaterThanOrEqual(1);

      const task = output[0];
      expect(task.id).toBeDefined();
      expect(task.title).toBeDefined();
      expect(task.status).toBeDefined();
      expect(task.agent).toBeDefined();
    });

    it('should show task ID, title, agent, workflow, status, and duration', async () => {
      // Create a task
      await runRover(['task', '-y', 'Create a hello world script', '--json']);

      // List tasks in JSON mode
      const listResult = await runRover(['list', '--json']);
      expect(listResult.exitCode).toBe(0);

      const output = JSON.parse(listResult.stdout);
      expect(output.length).toBeGreaterThanOrEqual(1);

      const task = output[0];
      expect(task.id).toBeDefined();
      expect(task.title).toBeDefined();
      expect(task.agent).toBeDefined();
      expect(task.status).toBeDefined();
    });
  });

  describe('global task listing', () => {
    it('should list tasks from all registered projects when run outside a project', async () => {
      // Create a task in the project
      await runRover(['task', '-y', 'Create a hello world script', '--json']);

      // Run list from outside the project with the --project flag
      const listResult = await runRover([
        'list',
        '--json',
        '--project',
        testDir,
      ]);

      expect(listResult.exitCode).toBe(0);

      const output = JSON.parse(listResult.stdout);
      expect(Array.isArray(output)).toBe(true);
      expect(output.length).toBeGreaterThanOrEqual(1);
    });
  });

  describe('JSON output', () => {
    it('should produce a JSON array of task objects with full metadata', async () => {
      // Create a task
      await runRover(['task', '-y', 'Create a hello world script', '--json']);

      // List tasks in JSON mode
      const listResult = await runRover(['list', '--json']);
      expect(listResult.exitCode).toBe(0);

      const output = JSON.parse(listResult.stdout);
      expect(Array.isArray(output)).toBe(true);

      if (output.length > 0) {
        const task = output[0];
        expect(task.id).toBeDefined();
        expect(task.title).toBeDefined();
        expect(task.status).toBeDefined();
      }
    });
  });

  /**
   * SKIPPED: Tests require real AI agent execution
   *
   * Why skipped:
   *   These tests require tasks to reach COMPLETED or FAILED status to trigger
   *   the onComplete hook. With mock Docker, containers don't actually run,
   *   so tasks never reach terminal status.
   *
   * TODO: To unskip these tests:
   *   1. Set ROVER_E2E_REAL_AGENT=true environment variable
   *   2. Ensure Docker is running and can pull the rover agent image
   *   3. Ensure a valid AI agent (Claude CLI, Gemini CLI, etc.) is installed and authenticated
   *   4. Run with: ROVER_E2E_REAL_AGENT=true pnpm e2e-test --grep "hook triggering"
   */
  describe.skipIf(SKIP_REAL_AGENT_TESTS)(
    'hook triggering on status change',
    () => {
      it('should trigger onComplete hook when a task reaches terminal status', async () => {
        const hookLogFile = join(testDir, 'hook-triggered.log');
        const roverConfig = JSON.parse(
          readFileSync(join(testDir, 'rover.json'), 'utf8')
        );
        roverConfig.hooks = {
          onComplete: [
            `echo "TASK_ID=$ROVER_TASK_ID STATUS=$ROVER_TASK_STATUS" > ${hookLogFile}`,
          ],
        };
        writeFileSync(
          join(testDir, 'rover.json'),
          JSON.stringify(roverConfig, null, 2)
        );
        await runRover([
          'task',
          '-y',
          'Create a hello world bash script named hello.sh',
        ]);

        const startTime = Date.now();
        const timeout = 600000;
        let taskCompleted = false;

        while (Date.now() - startTime < timeout) {
          const listResult = await runRover(['list', '--json']);
          if (listResult.exitCode === 0) {
            const tasks = JSON.parse(listResult.stdout);
            if (
              tasks.length > 0 &&
              (tasks[0].status === 'COMPLETED' || tasks[0].status === 'FAILED')
            ) {
              taskCompleted = true;
              break;
            }
          }
          await new Promise(resolve => setTimeout(resolve, 2000));
        }

        expect(taskCompleted).toBe(true);
        expect(existsSync(hookLogFile)).toBe(true);

        const hookLog = readFileSync(hookLogFile, 'utf8');
        expect(hookLog).toContain('TASK_ID=');
        expect(hookLog).toMatch(/STATUS=(completed|failed)/);
      });
    }
  );

  describe('alias support', () => {
    it('should work with the ls alias', async () => {
      // Create a task
      await runRover(['task', '-y', 'Create a hello world script', '--json']);

      // List using alias
      const listResult = await runRover(['ls', '--json']);
      expect(listResult.exitCode).toBe(0);

      const output = JSON.parse(listResult.stdout);
      expect(Array.isArray(output)).toBe(true);
    });
  });

  describe('watch mode', () => {
    it('should accept the --watch flag with default interval', async () => {
      // Create a task first
      await runRover(['task', '-y', 'Create a hello world script', '--json']);

      // Start watch mode with a very short timeout to verify it accepts the flag
      // We use AbortController to cancel after a brief moment since watch mode runs indefinitely
      const roverBin = join(__dirname, '../../../dist/index.mjs');
      const testPath = `${mockBinDir}:${originalPath}`;

      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 1000);

      try {
        await execa('node', [roverBin, 'list', '--watch'], {
          cwd: testDir,
          env: {
            PATH: testPath,
            HOME: mockHomeDir,
            USER: process.env.USER,
            TMPDIR: process.env.TMPDIR,
            ROVER_NO_TELEMETRY: '1',
          },
          signal: controller.signal,
          reject: false,
        });
      } catch (error: unknown) {
        // Expected to be aborted - this is fine
        if (
          error &&
          typeof error === 'object' &&
          'killed' in error &&
          error.killed
        ) {
          // Process was killed as expected
        }
      } finally {
        clearTimeout(timeoutId);
      }

      // If we got here without an error about invalid flags, the command accepts --watch
      expect(true).toBe(true);
    });

    it('should accept a custom interval argument (1-60 seconds)', async () => {
      // Create a task first
      await runRover(['task', '-y', 'Create a hello world script', '--json']);

      // Test with custom interval of 5 seconds
      const roverBin = join(__dirname, '../../../dist/index.mjs');
      const testPath = `${mockBinDir}:${originalPath}`;

      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 1000);

      try {
        await execa('node', [roverBin, 'list', '--watch', '5'], {
          cwd: testDir,
          env: {
            PATH: testPath,
            HOME: mockHomeDir,
            USER: process.env.USER,
            TMPDIR: process.env.TMPDIR,
            ROVER_NO_TELEMETRY: '1',
          },
          signal: controller.signal,
          reject: false,
        });
      } catch (error: unknown) {
        // Expected to be aborted
        if (
          error &&
          typeof error === 'object' &&
          'killed' in error &&
          error.killed
        ) {
          // Process was killed as expected
        }
      } finally {
        clearTimeout(timeoutId);
      }

      // If we got here without an error about invalid interval, it accepts custom intervals
      expect(true).toBe(true);
    });

    it('should accept minimum interval of 1 second', async () => {
      await runRover(['task', '-y', 'Create a hello world script', '--json']);

      const roverBin = join(__dirname, '../../../dist/index.mjs');
      const testPath = `${mockBinDir}:${originalPath}`;

      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 1000);

      try {
        await execa('node', [roverBin, 'list', '--watch', '1'], {
          cwd: testDir,
          env: {
            PATH: testPath,
            HOME: mockHomeDir,
            USER: process.env.USER,
            TMPDIR: process.env.TMPDIR,
            ROVER_NO_TELEMETRY: '1',
          },
          signal: controller.signal,
          reject: false,
        });
      } catch (error: unknown) {
        if (
          error &&
          typeof error === 'object' &&
          'killed' in error &&
          error.killed
        ) {
          // Process was killed as expected
        }
      } finally {
        clearTimeout(timeoutId);
      }

      expect(true).toBe(true);
    });

    it('should accept maximum interval of 60 seconds', async () => {
      await runRover(['task', '-y', 'Create a hello world script', '--json']);

      const roverBin = join(__dirname, '../../../dist/index.mjs');
      const testPath = `${mockBinDir}:${originalPath}`;

      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), 1000);

      try {
        await execa('node', [roverBin, 'list', '--watch', '60'], {
          cwd: testDir,
          env: {
            PATH: testPath,
            HOME: mockHomeDir,
            USER: process.env.USER,
            TMPDIR: process.env.TMPDIR,
            ROVER_NO_TELEMETRY: '1',
          },
          signal: controller.signal,
          reject: false,
        });
      } catch (error: unknown) {
        if (
          error &&
          typeof error === 'object' &&
          'killed' in error &&
          error.killed
        ) {
          // Process was killed as expected
        }
      } finally {
        clearTimeout(timeoutId);
      }

      expect(true).toBe(true);
    });
  });
});
